def unicode_caesar_decrypt(ciphertext, shift):
    decrypted = []
    for char in ciphertext:
        code = ord(char)
        # 只对中文字符范围进行解密 (基本汉字区: 0x4E00-0x9FFF)
        if 0x4E00 <= code <= 0x9FFF:
            new_code = code - shift
            # 处理超出汉字范围的情况
            if new_code < 0x4E00:
                new_code += 0x9FFF - 0x4E00 + 1
            decrypted.append(chr(new_code))
        else:
            decrypted.append(char)  # 非汉字保持原样
    return ''.join(decrypted)

